import React from "react";
import { Navbar } from "../index";
import "./CreateShipment.css";
import axios from "axios";

class CreateShipment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      RecipientName: "",
      ReceiverTel: "",
      Addrs: "",
      Province: "BKK",
      ItemType: "fruits",
      Weight: "",
      Height:"",
      Length:"",
      Width:"",
    };
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    this.calculate();
    console.log("Form Data:", this.state);
    const calculatedValue = this.calculate();
  };

  calculate = () => {
    const { RecipientName, ReceiverTel, Addrs, Province, ItemType, Weight, Height, Length, Width} = this.state;

    const totalWeight = parseFloat(Weight) * 2;

    return totalWeight; 
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSelectorChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <>
        <Navbar />
        <div className="Create-container" id="container">
          <div className="Create-form-container">
            <form onSubmit={this.handleSubmit}>
              <h1>Create Shipment</h1>
              <span>Enter shipment information to create shipment</span>
              <br />
              <div className="form-group row">
                <label
                  htmlFor="RecipientName"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Recipient name</span>
                </label>
                <div className="enterdata">
                  <input
                    name="RecipientName"
                    value={this.state.RecipientName}
                    onChange={this.handleChange}
                    type="text"
                    placeholder="Enter recipient name"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="ReceiverTel"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Mobile number</span>
                </label>
                <div className="enterdata">
                  <input
                    id="ReceiverTel"
                    name="ReceiverTel"
                    value={this.state.ReceiverTel}
                    onChange={this.handleChange}
                    type="tel"
                    placeholder="Enter recipient mobile number"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label htmlFor="Addrs" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Address details</span>
                </label>
                <div className="enterdata">
                  <input
                    id="Addrs"
                    name="Addrs"
                    value={this.state.Addrs}
                    onChange={this.handleChange}
                    type="text"
                    placeholder="Enter address details"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label htmlFor="Province" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">
                    Provinces/District/Sub-district/zip code
                  </span>
                </label>
                <div className="enterdata">
                  <select
                    id="Province"
                    name="Province"
                    value={this.state.Province}
                    onChange={this.handleSelectorChange}
                  >
                    <option value="BKK">BKK</option>
                    <option value="Samut prakarn">Samut prakarn</option>
                  </select>
                </div>
              </div>
              <div className="form-group row">
                <label htmlFor="ItemType" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Item Type</span>{" "}
                  <span className="required">*</span>
                </label>
                <div className="enterdata">
                  <select
                    id="ItemType"
                    name="ItemType"
                    value={this.state.ItemType}
                    onChange={this.handleSelectorChange}
                  >
                    <option value="fruits">fruits</option>
                    <option value="baby products">baby products</option>
                    <option value="clothing accessories">
                      clothing accessories
                    </option>
                    <option value="cosmetic/beauty">cosmetic/beauty</option>
                    <option value="fresh food">fresh food</option>
                    <option value="hand tools">hand tools</option>
                    <option value="health">health</option>
                    <option value="kitchenware">kitchenware</option>
                    <option value="tree">tree</option>
                    <option value="others">others</option>
                  </select>
                </div>
              </div>
              <br />
              <div className="form-group row">
                <label htmlFor="Weight" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Weight (kg)</span>
                </label>
                <div className="enterdata">
                  <input
                    id="Weight"
                    name="Weight"
                    value={this.state.Weight}
                    onChange={this.handleChange}
                    type="number"
                    placeholder="Enter weight in kilograms"
                  />
                </div>
              </div>
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      </>
    );
  }
}

export default CreateShipment;
