import React, { useState } from "react";
import axios from "axios";

function ShippingForm() {
  const [searched, setSearched] = useState(false);
  const [parcelNumber, setParcelNumber] = useState("");
  const [trackingInfo, setTrackingInfo] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:3333/tracking/${parcelNumber}`);
      const data = response.data;
      setTrackingInfo(data);
      setSearched(true);
      setError(null); // Reset error state
    } catch (error) {
      console.error("Error fetching tracking information", error);
      setError("Error fetching tracking information");
    }
  };

  return (
    <div>
      <input
        type="text"
        value={parcelNumber}
        onChange={(e) => setParcelNumber(e.target.value)}
        placeholder="Enter parcel number"
      />
      <button onClick={handleSearch}>Search</button>

      {/* Display error message if there's an error */}
      {error && <p>{error}</p>}

      {/* Display tracking information if available */}
      {searched && trackingInfo && (
        <div>
          <h2>Tracking Information</h2>
          <ul>
            {trackingInfo.map((event, index) => (
              <li key={index}>
                <p>Date: {event.date}</p>
                <p>Time: {event.time}</p>
                <p>Location: {event.location}</p>
                <p>Status: {event.status}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShippingForm;
