import React, { useState, useEffect } from "react";
import "./Admin.css";
import { Navbar } from "../index";
import axios from "axios";

function Admin() {
  const [parcels, setParcels] = useState([]);
  const [newParcel, setNewParcel] = useState({
    parcel_ID: "",
    weight: "",
    type: "",
    shipping_cost: "",
    parcel_sender: "",
    parcel_receiver: "",
    parcel_branch: ""
  });

  useEffect(() => {
    fetchParcels();
  }, []);

  const fetchParcels = () => {
    axios
      .get("http://localhost:3333/Admin")
      .then((res) => {
        setParcels(res.data);
      })
      .catch((err) => console.log(err));
  };

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const updatedParcels = [...parcels];
    updatedParcels[index][name] = value;
    setParcels(updatedParcels);
  };

  const saveParcel = (index) => {
    axios
      .put(`http://localhost:3333/Admin/${parcels[index].parcel_ID}`, parcels[index])
      .then((res) => {
        fetchParcels();
      })
      .catch((err) => console.log(err));
  };

  const deleteParcel = (id) => {
    axios
      .delete(`http://localhost:3333/Admin/${id}`)
      .then((res) => {
        fetchParcels();
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <div>
        <div className="Admin-Container">
          <div className="Admin-table-container">
            <div>
              <input
                type="text"
                name="parcel_ID"
                value={newParcel.parcel_ID}
                onChange={(e) => setNewParcel({...newParcel, parcel_ID: e.target.value})}
                placeholder="Parcel ID"
              />
              <input
                type="text"
                name="weight"
                value={newParcel.weight}
                onChange={(e) => setNewParcel({...newParcel, weight: e.target.value})}
                placeholder="Weight"
              />
              {/* Add more input fields for other properties */}
              <button className="btn btn-success" onClick={() => addParcel()}>
                Add +
              </button>
            </div>
            <table className="table">
              <thead>
                <tr>
                  <th>Parcel ID</th>
                  <th>Weight</th>
                  <th>Type</th>
                  <th>Shipping Cost</th>
                  <th>Sender</th>
                  <th>Receiver</th>
                  <th>Branch</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {parcels.map((parcel, index) => (
                  <tr key={parcel.parcel_ID}>
                    <td>{parcel.parcel_ID}</td>
                    <td>
                      <input
                        type="text"
                        name="weight"
                        value={parcel.weight}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="type"
                        value={parcel.type}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="shipping_cost"
                        value={parcel.shipping_cost}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="parcel_sender"
                        value={parcel.parcel_sender}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="parcel_receiver"
                        value={parcel.parcel_receiver}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <input
                        type="text"
                        name="parcel_branch"
                        value={parcel.parcel_branch}
                        onChange={(e) => handleInputChange(e, index)}
                      />
                    </td>
                    <td>
                      <button onClick={() => saveParcel(index)}>Save</button>
                      <button onClick={() => deleteParcel(parcel.parcel_ID)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Admin;
