import React, { useState } from "react";
import { Navbar } from "../index";
import arrow from "./arrow-circle.svg";
import "./Tracking.css";

function ShippingForm() {
  const [searched, setSearched] = useState(false);
  const [trackingData, setTrackingData] = useState(null); // State to hold fetched tracking data
  const [trackingNumber, setTrackingNumber] = useState(""); // State to hold the entered tracking number

  const handleSearch = async () => {
    // Make an API call to fetch tracking data
    try {
      const response = await fetch(`/api/track?trackingNumber=${trackingNumber}`);
      if (response.ok) {
        const data = await response.json();
        setTrackingData(data); // Update state with fetched tracking data
        setSearched(true);
      } else {
        // Handle error response
        console.error("Failed to fetch tracking data:", response.status);
      }
    } catch (error) {
      console.error("Error fetching tracking data:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="Tracking-container" id="container">
        <form>
          {!searched && (
            <div className="Tracking-Header">
              <h1>Track Parcel</h1>
              <span>Enter tracking number to trace your parcel's number</span>
              <br />
              <br />
            </div>
          )}
          <div className="search-bar">
            <input
              className="form tracking"
              type="text"
              placeholder="Tracking No."
              value={trackingNumber}
              onChange={(e) => setTrackingNumber(e.target.value)}
            />
            <button
              type="button"
              className="btn tracking"
              style={{ marginTop: "7px" }}
              onClick={handleSearch}
            >
              Search
            </button>
          </div>
          {/* Render tracking data if available */}
          {searched && trackingData && (
            <div>
              <p>Latest status</p>
              <br />
              <ul className="events">
                {/* Render tracking events here */}
              </ul>
            </div>
          )}
        </form>
      </div>
    </div>
  );
}

export default ShippingForm;
