import React, { useState, useEffect } from "react";
import { Navbar } from "../index";
import "./CreateShipment.css";
import axios from "axios";

const CreateShipment = () => {
  const [provinces, setProvinces] = useState([]);
  const [formData, setFormData] = useState({
    parcel_ID: "1",
    RecipientName: "",
    parcel_sender: "",
    parcel_receiver: "",
    shipping_cost: "",
    type: "",
    weight: "",
    Province_Origin: "",
    Province_Destination: ""
  });

  useEffect(() => {
    fetchProvinces();
  }, []);

  const fetchProvinces = () => {
    fetch("http://localhost:3333/Shipping")
      .then((response) => response.json())
      .then((data) => {
        if (data.status === "ok") {
          setProvinces(data.provinces);
        } else {
          console.error("Error fetching provinces:", data.message);
        }
      })
      .catch((error) => {
        console.error("Error fetching provinces:", error);
      });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const calculatedValue = calculate();
    console.log("Form Data:", formData);

    try {
      const response = await axios.post("/create", formData, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      console.log("Response from backend:", response.data);
    } catch (error) {
      console.error("Error creating shipment:", error);
    }
  };

  fetchProvinces = () => {
    fetch("http://localhost:3333/Shipping")
      .then((response) => response.json())
      .then((data) => {
        if (data.status === "ok") {
          setProvinces(data.provinces);
        } else {
          console.error("Error fetching provinces:", data.message);
        }
      })
      .catch((error) => {
        console.error("Error fetching provinces:", error);
      });
  };

  calculate = () => {
    const {
      Province_Destination,
      Province_Origin,
      Weight,
      Height,
      Length,
      Width,
    } = this.state;
    let price = 0;
    const distance = Math.abs(Province_Origin - Province_Destination);
    const size = parseFloat(Width) + parseFloat(Height) + parseFloat(Length);

    if (size <= 40 && Weight <= 2) {
      if (distance <= 5) {
        price = 35;
      } else if (distance > 5) {
        price = 55;
      }
    } else if (size <= 75 && Weight <= 7) {
      if (distance <= 5) {
        price = 65;
      } else if (distance > 5) {
        price = 85;
      }
    } else if (size <= 105 && Weight <= 10) {
      if (distance <= 5) {
        price = 130;
      } else if (distance > 5) {
        price = 145;
      }
    } else if (size <= 120 && Weight <= 15) {
      if (distance <= 5) {
        price = 185;
      } else if (distance > 5) {
        price = 205;
      }
    } else if (size <= 150 && Weight <= 20) {
      if (distance <= 5) {
        price = 290;
      } else if (distance > 5) {
        price = 330;
      }
    } else if (size <= 280 && Weight <= 30) {
      if (distance <= 5) {
        price = 380;
      } else if (distance > 5) {
        price = 420;
      }
    } else {
      alert("Cannot used this Package Type!!!");
    }
    return price;
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSelectorChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  return (
    <>
      <Navbar />
      <div className="Create-container" id="container">
        <div className="Create-form-container">
          <form onSubmit={this.handleSubmit}>
            <h1>Create Shipment</h1>
            <span>Enter shipment information to create shipment</span>
            <br />
            <div className="form-group row">
              <label
                htmlFor="RecipientName"
                className="col-sm-4 col-form-label"
              >
                <span className="lb_IDCard">Recipient name</span>
              </label>
              <div className="enterdata">
                <input
                  name="RecipientName"
                  value={this.state.RecipientName}
                  onChange={this.handleChange}
                  type="text"
                  placeholder="Enter recipient name"
                />
              </div>
            </div>
            <div className="form-group row">
              <label htmlFor="ReceiverTel" className="col-sm-4 col-form-label">
                <span className="lb_IDCard">Mobile number</span>
              </label>
              <div className="enterdata">
                <input
                  id="ReceiverTel"
                  name="ReceiverTel"
                  value={this.state.parcel_receiver}
                  onChange={this.handleChange}
                  type="tel"
                  placeholder="Enter recipient mobile number"
                />
              </div>
            </div>
            <div className="form-group row">
              <label htmlFor="ReceiverTel" className="col-sm-4 col-form-label">
                <span className="lb_IDCard">Sender number</span>
              </label>
              <div className="enterdata">
                <input
                  id="ReceiverTel"
                  name="ReceiverTel"
                  value={this.state.parcel_sender}
                  onChange={this.handleChange}
                  type="tel"
                  placeholder="Enter recipient mobile number"
                />
              </div>
            </div>
            <div className="form-group row">
              <label htmlFor="Addrs" className="col-sm-4 col-form-label">
                <span className="lb_IDCard">Address details</span>
              </label>
              <div className="enterdata">
                <input
                  id="Addrs"
                  name="Addrs"
                  value={this.state.Addrs}
