import React from "react";
import { Navbar } from "../index";
import "./CreateShipment.css";
import axios from "axios";
import { parse } from "@fortawesome/fontawesome-svg-core";

class CreateShipment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      parcel_ID:"1",
      RecipientName: "",
      parcel_sender: "",
      parcel_receiver:"",
      shipping_cost:"",
      type:"",
      weight:"",
      Province_Origin:"",
      Province_Destination:""
    };
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    const calculatedValue = this.calculate();
    console.log("Form Data:", this.state);

    // Prepare the data to be sent to the backend
    const data = {
      RecipientName: this.state.RecipientName,
      parcel_receiver: this.state.parcel_receiver,
      parcel_sender: this.state.parcel_sender,
      Addrs: this.state.Addrs,
      Province_Origin: this.state.Province_Origin, 
      Province_Destination: this.state.Province_Destination, 
      ItemType: this.state.type,
      Weight: this.state.weight,
    };

    try {
      const response = await axios.post("/create", data, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      console.log("Response from backend:", response.data);
    } catch (error) {
      console.error("Error creating shipment:", error);
    }
  };



  calculate = () => {
    const {
      Province_Destination,
      Province_Origin,
      Weight,
      Height,
      Length,
      Width,
    } = this.state;
    let price = 0;
    const distance = Math.abs(ProvinceOrigin - Province_Destination );
    const size = parseFloat(Width) + parseFloat(Height) + parseFloat(Length);

    if (size <= 40 && Weight <= 2) {
      if (distance <= 5) {
        price = 35;
      } else if (distance > 5) {
        price = 55;
      }
    } else if (size <= 75 && Weight <= 7) {
      if (distance <= 5) {
        price = 65;
      } else if (distance > 5) {
        price = 85;
      }
    } else if (size <= 105 && Weight <= 10) {
      if (distance <= 5) {
        price = 130;
      } else if (distance > 5) {
        price = 145;
      }
    } else if (size <= 120 && Weight <= 15) {
      if (distance <= 5) {
        price = 185;
      } else if (distance > 5) {
        price = 205;
      }
    } else if (size <= 150 && Weight <= 20) {
      if (distance <= 5) {
        price = 290;
      } else if (distance > 5) {
        price = 330;
      }
    } else if (size <= 280 && Weight <= 30) {
      if (distance <= 5) {
        price = 380;
      } else if (distance > 5) {
        price = 420;
      }
    } else {
      alert("Cannot used this Package Type!!!");
    }
    return price;
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSelectorChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <>
        <Navbar />
        <div className="Create-container" id="container">
          <div className="Create-form-container">
            <form onSubmit={this.handleSubmit}>
              <h1>Create Shipment</h1>
              <span>Enter shipment information to create shipment</span>
              <br />
              <div className="form-group row">
                <label
                  htmlFor="RecipientName"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Recipient name</span>
                </label>
                <div className="enterdata">
                  <input
                    name="RecipientName"
                    value={this.state.RecipientName}
                    onChange={this.handleChange}
                    type="text"
                    placeholder="Enter recipient name"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="ReceiverTel"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Mobile number</span>
                </label>
                <div className="enterdata">
                  <input
                    id="ReceiverTel"
                    name="ReceiverTel"
                    value={this.state.parcel_receiver}
                    onChange={this.handleChange}
                    type="tel"
                    placeholder="Enter recipient mobile number"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="ReceiverTel"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Sender number</span>
                </label>
                <div className="enterdata">
                  <input
                    id="ReceiverTel"
                    name="ReceiverTel"
                    value={this.state.parcel_sender}
                    onChange={this.handleChange}
                    type="tel"
                    placeholder="Enter recipient mobile number"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label htmlFor="Addrs" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Address details</span>
                </label>
                <div className="enterdata">
                  <input
                    id="Addrs"
                    name="Addrs"
                    value={this.state.Addrs}
                    onChange={this.handleChange}
                    type="text"
                    placeholder="Enter address details"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label htmlFor="Province" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">
                    Provinces/District/Sub-district/zip code
                  </span>
                </label>
                <div className="enterdata">
                  <select
                    id="Province_Origin"
                    name="Province_Origin"
                    value={this.state.Province_Origin}
