import React from "react";
import { Navbar } from "../index";
import "./CreateShipment.css";
import axios from "axios";
import { parse } from "@fortawesome/fontawesome-svg-core";

class CreateShipment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      parcel_ID: "1",
      RecipientName: "",
      parcel_sender: "",
      parcel_receiver: "",
      shipping_cost: "",
      type: "",
      weight: "",
      Province_Origin: "",
      Province_Destination: "",
      provinces: [],
    };
  }

  componentDidMount() {
    this.fetchProvinces();
  }

  fetchProvinces = () => {
    fetch("http://localhost:3333/Shipping")
      .then((response) => response.json())
      .then((data) => {
        if (data.status === "ok") {
          this.setState({ provinces: data.provinces });
        } else {
          console.error("Error fetching provinces:", data.message);
        }
      })
      .catch((error) => {
        console.error("Error fetching provinces:", error);
      });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const calculatedValue = this.calculate();
    console.log("Form Data:", this.state);

    // Prepare the data to be sent to the backend
    const data = {
      RecipientName: this.state.RecipientName,
      parcel_receiver: this.state.parcel_receiver,
      parcel_sender: this.state.parcel_sender,
      Addrs: this.state.Addrs,
      Province_Origin: this.state.Province_Origin,
      Province_Destination: this.state.Province_Destination,
      ItemType: this.state.type,
      Weight: this.state.weight,
    };

    try {
      const response = await axios.post("/create", data, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      console.log("Response from backend:", response.data);
    } catch (error) {
      console.error("Error creating shipment:", error);
    }
  };

  calculate = () => {
    const { Province_Destination, Province_Origin, Weight } = this.state;
    let price = 0;
    const distance = Math.abs(Province_Origin - Province_Destination);

    if (Weight <= 2) {
      if (distance <= 5) {
        price = 35;
      } else if (distance > 5) {
        price = 55;
      }
    } else if (Weight <= 7) {
      if (distance <= 5) {
        price = 65;
      } else if (distance > 5) {
        price = 85;
      }
    } else if (Weight <= 10) {
      if (distance <= 5) {
        price = 130;
      } else if (distance > 5) {
        price = 145;
      }
    } else if (Weight <= 15) {
      if (distance <= 5) {
        price = 185;
      } else if (distance > 5) {
        price = 205;
      }
    } else if (Weight <= 20) {
      if (distance <= 5) {
        price = 290;
      } else if (distance > 5) {
        price = 330;
      }
    } else if (Weight <= 30) {
      if (distance <= 5) {
        price = 380;
      } else if (distance > 5) {
        price = 420;
      }
    } else {
      alert("Cannot used this Package Type!!!");
    }
    return price;
  };

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSelectorChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <>
        <Navbar />
        <div className="Create-container" id="container">
          <div className="Create-form-container">
            <form onSubmit={this.handleSubmit}>
              <h1>Create Shipment</h1>
              <span>Enter shipment information to create shipment</span>
              <br />
              <div className="form-group row">
                <label
                  htmlFor="RecipientName"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Recipient name</span>
                </label>
                <div className="enterdata">
                  <input
                    name="RecipientName"
                    value={this.state.RecipientName}
                    onChange={this.handleChange}
                    type="text"
                    placeholder="Enter recipient name"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="parcel_receiver"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Mobile number</span>
                </label>
                <div className="enterdata">
                  <input
                    id="parcel_receiver"
                    name="parcel_receiver"
                    value={this.state.parcel_receiver}
                    onChange={this.handleChange}
                    type="tel"
                    placeholder="Enter recipient mobile number"
                  />
                </div>
              </div>
              <div className="form-group row">
                <label
                  htmlFor="parcel_receiver"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Sender number</span>
                </label>
                <div className="enterdata">
                  <input
                    id="parcel_sender"
                    name="parcel_sender"
                    value={this.state.parcel_sender}
                    onChange={this.handleChange}
                    type="tel"
                    placeholder="Enter recipient mobile number"
                  />
                </div>
