import React, { useState } from "react";
import { Navbar } from "../index";
import "./Complaints.css";
import axios from "axios";

class Complaints extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      formData: {
        appeal_ID: "1",
        appeal_type: "",
        appeal_des: "",
        appeal_usertel: "",
        appeal_track: "" // Add this field to match your form
      },
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleFileChange = this.handleFileChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState((prevState) => ({
      formData: {
        ...prevState.formData,
        [name]: value,
      },
    }));
  }

  handleFileChange(event) {
    const file = event.target.files[0]; // Assuming single file upload
    this.setState((prevState) => ({
      formData: {
        ...prevState.formData,
        appeal_img: file,
      },
    }));
  }

  handleSubmit(event) {
    event.preventDefault(); // Prevent default form submission behavior
    const { formData } = this.state;
    console.log("formData:", formData); // Check formData before sending

    axios.post("http://localhost:3333/complaints", formData)
      .then(response => {
        console.log("Response:", response.data);
        // Optionally, reset the form after successful submission
        this.setState({
          formData: {
            appeal_ID: "1",
            appeal_type: "",
            appeal_des: "",
            appeal_usertel: "",
            appeal_track: "" 
          }
        });
      })
      .catch(error => {
        console.error("Error:", error);
      });
  }


  render() {
    const { formData } = this.state;
    return (
      <>
        <Navbar />
        <div className="Complaints-container">
          <div className="Complaints-form-container">
            <form onSubmit={this.handleSubmit}>
              <h1>Complaints</h1>
              <span>Complain about your problem and explain details</span>
              <br />
              <br />
              <div className="form-group-row">
                <label
                  htmlFor="problemType"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Select problem type</span>
                </label>
                <div className="enterdata">
                  <select
                    id="problemType"
                    name="appeal_type"
                    value={formData.appeal_type}
                    onChange={this.handleChange}
                  >
                    <option value="0">Transport time problem</option>
                    <option value="1">Poor service attitude</option>
                    <option value="2">Branch doesn't answer the phone</option>
                    <option value="3">Courier doesn't answer the phone</option>
                    <option value="4">Other problem</option>
                  </select>
                </div>
              </div>
              <br />
              <div className="form-group-row">
                <label htmlFor="trackingNo" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Telephone Number</span>{" "}
                  <span className="required">*</span>
                </label>
                <div className="enterdata">
                  <input
                    type="text"
                    id="appeal_usertel"
                    name="appeal_usertel"
                    value={formData.appeal_usertel}
                    onChange={this.handleChange}
                    placeholder="Telephone Number"
                  />
                </div>
              </div>
              <br />
              <div className="form-group-row">
                <label htmlFor="trackingNo" className="col-sm-4 col-form-label">
                  <span className="lb_IDCard">Tracking No.</span>{" "}
                  <span className="required">*</span>
                </label>
                <div className="enterdata">
                  <input
                    type="text"
                    id="appeal_track"
                    name="appeal_track"
                    value={formData.appeal_track}
                    onChange={this.handleChange}
                    placeholder="Tracking No."
                  />
                </div>
              </div>
              <br/>
              <div className="form-group-row">
                <label
                  htmlFor="description"
                  className="col-sm-4 col-form-label"
                >
                  <span className="lb_IDCard">Description</span>{" "}
                  <span className="required">*</span>
                </label>
                <div className="enterdata2">
                  <input
                    type="text"
                    id="description"
                    name="appeal_des" // Add name attribute
                    value={formData.appeal_des}
                    onChange={this.handleChange}
                    placeholder="Let us know your problems as detailed as possible so that we can assist you better"
                  />
                </div>
              </div>
              <br />
              <br />
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      </>
    );
  }
}

export default Complaints;
