import React, { useState, useEffect } from "react";
import "./Admin.css";
import { Navbar } from "../index";
import axios from "axios";

function Admin() {
  const [parcels, setParcels] = useState([]);
  const [editingRow, setEditingRow] = useState(null); // State to track the row being edited
  const [newParcel, setNewParcel] = useState({
    parcel_ID: "",
    weight: "",
    type: "",
    shipping_cost: "",
    parcel_sender: "",
    parcel_receiver: "",
    parcel_branch: ""
  });

  useEffect(() => {
    axios
      .get("http://localhost:3333/Admin")
      .then((res) => {
        setParcels(res.data); 
      })
      .catch((err) => console.log(err));
  }, []);

  const handleEdit = (index) => {
    setEditingRow(index);
  };

  const handleConfirm = (index) => {
    const editedParcel = parcels[index];
    axios
      .put(`http://localhost:3333/Admin/${editedParcel.parcel_ID}`, editedParcel)
      .then((res) => {
        setEditingRow(null); // Reset editing state after confirmation
      })
      .catch((err) => console.log(err));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewParcel({
      ...newParcel,
      [name]: value
    });
  };

  const handleAddParcel = () => {
    axios
      .post("http://localhost:3333/Admin", newParcel)
      .then((res) => {
        // After successfully adding the parcel, fetch the updated list of parcels
        axios.get("http://localhost:3333/Admin")
          .then((res) => {
            setParcels(res.data);
          })
          .catch((err) => console.log(err));
        // Reset new parcel state
        setNewParcel({
          parcel_ID: "",
          weight: "",
          type: "",
          shipping_cost: "",
          parcel_sender: "",
          parcel_receiver: "",
          parcel_branch: ""
        });
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <div>
        <div className="Admin-Container">
          <div className="Admin-table-container">
            <button className="btn btn-success">Add +</button>
            <table className="table">
              <thead>
                <tr>
                  <th>Parcel ID</th>
                  <th>Weight</th>
                  <th>Type</th>
                  <th>Shipping Cost</th>
                  <th>Sender</th>
                  <th>Receiver</th>
                  <th>Branch</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {parcels.map((parcel, index) => (
                  <tr key={parcel.parcel_ID}>
                    <td>{editingRow === index ? <input type="text" value={parcel.parcel_ID} /> : parcel.parcel_ID}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.weight} /> : parcel.weight}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.type} /> : parcel.type}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.shipping_cost} /> : parcel.shipping_cost}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.parcel_sender} /> : parcel.parcel_sender}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.parcel_receiver} /> : parcel.parcel_receiver}</td>
                    <td>{editingRow === index ? <input type="text" value={parcel.parcel_branch} /> : parcel.parcel_branch}</td>
                    <td>
                      {editingRow === index ? (
                        <button onClick={() => handleConfirm(index)}>Confirm</button>
                      ) : (
                        <button onClick={() => handleEdit(index)}>Edit</button>
                      )}
                      <button>Delete</button>
                    </td>
                  </tr>
                ))}
                <tr>
                  <td><input type="text" name="parcel_ID" value={newParcel.parcel_ID} onChange={handleChange} /></td>
                  <td><input type="text" name="weight" value={newParcel.weight} onChange={handleChange} /></td>
                  <td><input type="text" name="type" value={newParcel.type} onChange={handleChange} /></td>
                  <td><input type="text" name="shipping_cost" value={newParcel.shipping_cost} onChange={handleChange} /></td>
                  <td><input type="text" name="parcel_sender" value={newParcel.parcel_sender} onChange={handleChange} /></td>
                  <td><input type="text" name="parcel_receiver" value={newParcel.parcel_receiver} onChange={handleChange} /></td>
                  <td><input type="text" name="parcel_branch" value={newParcel.parcel_branch} onChange={handleChange} /></td>
                  <td><button onClick={handleAddParcel}>Add</button></td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Admin;
