import React, { useState, useEffect } from "react";
import { Navbar } from "../index";
import "./Pickup.css";

function Pickup() {
  const [formData, setFormData] = useState({
    parcel: "0",
    pickupDate: "",
    pickupTime: "0",
    pickupLocation: "",
    province: "0",
  });

  const [locations, setLocations] = useState([]); // State to store location data

  useEffect(() => {
    // Fetch data when the component mounts
    fetch("/pickup")
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error("Failed to fetch data");
      })
      .then((data) => {
        // Assuming data is an array of location objects with id and name properties
        setLocations(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []); // Empty dependency array ensures this effect runs only once, on mount

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("your-backend-api-endpoint", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          console.log("Data submitted successfully");
          setFormData({
            parcel: "0",
            pickupDate: "",
            pickupTime: "0",
            pickupLocation: "",
            province: "0",
          });
        } else {
          console.error("Failed to submit data");
        }
      })
      .catch((error) => {
        console.error("Error occurred:", error);
      });
  };

  return (
    <div>
      <Navbar />
      <div className="Pickup-container" id="container">
        <div className="Pickup-form-container">
          <form onSubmit={handleSubmit}>
            <h1 className="header">Pickup request</h1>
            <span>select location and date for pickup service</span>
            <br />
            <br />

            {/* Parcel selection */}
            <div className="form-group row">
              <label className="col-sm-4 col-form-label">
                <span className="lb_IDCard">Select parcel</span>
              </label>
              <div className="enterdata">
                <select
                  name="parcel"
                  value={formData.parcel}
                  onChange={handleInputChange}
                >
                  {/* Populate options from state */}
                  {locations.map((location) => (
                    <option key={location.id} value={location.id}>
                      {location.parcel_ID}
                    </option>
                  ))}
                </select>
              </div>
            </div>
            <br />


          </form>
        </div>
      </div>
    </div>
  );
}

export default Pickup;
